    //ゴールがどのマスにあるのか
    //ゴールチェックとゴール表示に必要
    val goalXMasu = 118
    val goalYmasu = 4

    fun goalCheck(jiki:Jiki):Boolean {
        val xx = MASU_SIZE*goalXMasu
        val yy = MASU_SIZE*goalYmasu

        val vx = xx - jiki.sekaipos.x
        val vy = yy - jiki.sekaipos.y

        val kyori = Math.sqrt((vx * vx) + (vy * vy) .toDouble())
        val atarikyori = (jiki.ookisa/4).toDouble()

        if (kyori < atarikyori){
            return true
        }else{
            return false
        }
    }


    fun drawMapGoal(canvas: Canvas,x:Int,y:Int,jiki:Jiki){
        val goalIro = Paint()
        goalIro.style = Paint.Style.FILL
        goalIro.color = argb(255, 255, 30, 30)
        val xx = MASU_SIZE*goalXMasu+(360-jiki.sekaipos.x)
        val yy = MASU_SIZE*goalYmasu
        canvas.drawRect(shikakuRectXY(xx,yy,MASU_SIZE),goalIro)
    }

    fun drawMap(canvas: Canvas, row:Int, col:Int, masushurui:Int, jiki:Jiki){
        //呼ばれるときに、360-sekaixってなってるのはなんだろうか？　まぁいまんとこはいいか。
        // map.drawMap(canvas,i,j,map.masShurui(i,j),360-jiki.sekaipos.x)//
        val iro = Paint()
        iro.style = Paint.Style.FILL
        iro.color = argb(255, 30, 30, 30)
        when (masushurui) {
            TYPE_SORA -> { iro.color = argb(255, 155, 155, 250) }
            TYPE_ISHI -> { iro.color = argb(255, 80, 80, 80)}
        }
        //ここまでは色の設定

        // jikiにvar sekaipos = Vec2D(360,400)として、作るときに360を与えている。
        //それを引いてるのかな？
        // map.drawMap(canvas,i,j,map.masShurui(i,j),360-jiki.sekaipos.x)

        val xx = MASU_SIZE*col+(360-jiki.sekaipos.x)
        val yy = MASU_SIZE*row
        canvas.drawRect(shikakuRectXY(xx,yy,MASU_SIZE),iro)
    }

    fun shikakuRectXY(x:Int,y:Int,ookisa:Int): Rect {
        val left = x
        val right = x  + ookisa
        val top = y
        val bottom = y + ookisa
        val m = Rect(left, top, right,bottom)
        return m
    }


    fun masShurui(row:Int,col:Int): Int {
        return masu[row][col]
    }

    //マップの右端を計算する処理
    fun migiMax():Int{
        return (masu[0].size-2) * MASU_SIZE
    }
